import '../styles/app.css';
import React, { useState } from 'react';

const divTableCell = {
  display: 'table-cell',
};

const labelSize = {
  fontSize: 15,
}
const domainInputform = {
  fontSize: 12, 
  padding: 1, 
  width: 30 }

function InputParameters() {

  const [message, setMessage] = useState("");
  const [dispersaType, setDispersal] = useState('gaussian');
  const [hostNumber, setHostNumber] = useState(0);

  let handleSubmitResp = async (e) => {
    e.preventDefault();
    try {
      let res = await fetch("http://localhost:5000", 
      { 
        method: "POST",
        body: JSON.stringify({"dispersal type": dispersaType}),
      });
      console.log(res)
      if (res.status === 200) {
        setMessage("User created successfully");
      } else {
        setMessage("Some error occured");
        // todo address CORs issue on api gateway...
      }
    } catch (err) {
      console.log(err);
      alert(`${message}, dispersal type is ${dispersaType}`)
    }
  };

  return (
    <div className='inputParamPanel'>
      <form onSubmit={handleSubmitResp} className='input-param-form'>
        <label style={labelSize} > Dispersal type </label> 
        <select value={dispersaType} onChange={e => setDispersal(e.target.value)} style={{width: 150}}> 
          <option value="gaussian">Gaussian</option>  
          <option value="exponential">Exponential</option>
          <option value="inverse_power_Law">Inverse power law</option>
        </select>
        <div className='in-line'>
          <label style={labelSize} lassName='in-line'> Domain size </label>
          <span className='in-table'>
            <div style={divTableCell}> <input type="text" style={domainInputform} /> </div>
            <div style={divTableCell}> <input type="text" style={domainInputform} /> </div> 
          </span>
        </div>
        <div className='in-line'> 
          <label style={labelSize}> Host number: {hostNumber} </label>
          <input type="range" min="0" max="1000" value={hostNumber}  onChange={e => setHostNumber(e.target.value)}/>   
        </div>
        <input type="submit" value="Simulate" />
      </form>
    </div>
  );
}

export default InputParameters;
