import './App.css';
import React, { useState, useRef} from 'react';
import { ProgressBar } from 'react-bootstrap';
import myVideo from './media/file_example_MP4_480_1_5MG.mp4';

function App() {

  const vidRef = useRef(null);
  const buttonRef = useRef(null)

  const onButtonClick = () => {
    // if (isNaN(buttonRef))
    //   return;

    if (vidRef.current.paused) {
       vidRef.current.play()
    }
    else {
      vidRef.current.pause()
    }
  }


  const [played, setProgressBar] = useState(0);

  const handelProgress = () => { 
    if (isNaN(vidRef))   // duration is NotaNumber at Beginning.
      return;

    var percentage = vidRef.current.currentTime;
    setProgressBar(percentage);
  }


  return (

    <div className="App">
      <header className="App-header">
      <video ref={vidRef}>
      </video>
        <video ref={vidRef} src={myVideo} type="video/mp4"> </video>
        <button ref={buttonRef} onClick={() => onButtonClick()} >  
          &gt;
        </button>

        {/* <progress value={progress} max="100"> </progress> add a progress bar*/}

      </header>
    </div>
  );
}

export default App;
