import '../styles/app.css';
import React, { useState } from 'react';


function InputParameters() {

  console.log('rendering input forms')
  const [name, setName] = useState("");
  const [dispersaType, setDispersal] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = (e) => {
      // evt.preventDefault();
      setName(e.target.value)
      alert(`Submitting Name ${dispersaType}`)
  }

  let handleSubmitResp = async (e) => {
    e.preventDefault();
    try {
      let res = await fetch("https://9r5kx81fpi.execute-api.eu-west-2.amazonaws.com/dev/", {
        method: "POST",
        body: JSON.stringify({
          name: name,
        }),
      });
      if (res.status === 200) {
        console.log(res)
        setMessage("User created successfully");
      } else {
        setMessage("Some error occured");
        // todo address CORs issue on api gateway...
      }
    } catch (err) {
      console.log(err);
      alert(`${message}`)

    }
  };


  return (
    <div className='inputParamPanel'>
    <form onSubmit={handleSubmitResp}>
      <label> 
        Dispersal type
        <select value={{name}} onChange={e => setDispersal(e.target.value)}> 
          <option value="gaussian">Gaussian</option>
          <option value="exponential">Exponential</option>
          <option value="inversePowerLaw">Inverse power law</option>
        </select>
      </label>

      <input type="submit" value="Simulate" />
    </form>
    </div>
  );
}

export default InputParameters;
