import './App.css';
import ReactPlayer from 'react-player';
import myVideo from './media/file_example_MP4_480_1_5MG.mp4';


function App() {
  var video = document.getElementById("Video1");



  // video.addEventListener('timeupdate', updateProgressBar, false);


  // function updateProgressBar() { 
  // var progressBar = document.getElementById('progressbar'); 
  // var percentage = Math.floor((100 / mediaPlayer.duration) * mediaPlayer.currentTime);
  // progressBar.value = percentage; progressBar.innerHTML = percentage + '% played'; 
  // }

  function vidplay() {

    var button = document.getElementById("play");
    if (video.paused) {
      video.play();
      button.textContent = "||";
    } else {
      video.pause();
      button.textContent = ">";
    }
  }

  return (
    <div className="App">
      <header className="App-header">

        <video id='Video1' src={myVideo}> </video>
        <button id="play" 
          onClick={function(e) {
            this.vidplay(); //can pass arguments this.btnTapped(foo, bar);          
          }} >  
          &gt;
        </button>
        <progress id="progressbar" value="0" max="100"></progress>

        <p>
          Sim text will goe here...
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
