import '../styles/app.css';
import 'katex/dist/katex.min.css';
import React, { useState } from 'react';
import { InlineMath, BlockMath } from 'react-katex';

const divTableCell = {
  display: 'table-cell',
};

const labelSize = {
  fontSize: 15
}
const domainInputform = {
  fontSize: 12, 
  padding: 1, 
  width: 30 ,
  marginLeft: 30}

  const domainInputformR = {
    fontSize: 12, 
    padding: 1, 
    width: 30,
    marginLeft: 4 }

function InputParameters() {

  const [message, setMessage] = useState("");
  const [dispersaType, setDispersal] = useState('gaussian');
  const [hostNumber, setHostNumber] = useState(0);
  const [secondaryR0, setSecondaryR0] = useState(0);
  const [infectiousLT, setInfectiousLT] = useState(0);
  const [simulationRT, setSimulationRT] = useState(0);

  const secondaryR0Label = 'R\_{0}';


  let handleSubmitResp = async (e) => {
    e.preventDefault();
    try {
      let res = await fetch("http://localhost:5000", 
      { 
        method: "POST",
        body: JSON.stringify({"dispersal type": dispersaType}),
      });
      console.log(res)
      if (res.status === 200) {
        setMessage("User created successfully");
      } else {
        setMessage("Some error occured");
        // todo address CORs issue on api gateway...
      }
    } catch (err) {
      console.log(err);
      alert(`${message}, dispersal type is ${dispersaType}`)
    }
  };

  return (
      <div className='inputParamPanel'>
        <form onSubmit={handleSubmitResp} style={{width: 250}}>
          <label style={labelSize} > Dispersal type </label> 

          <select value={dispersaType} onChange={e => setDispersal(e.target.value)}> 
            <option value="gaussian">Gaussian</option>  
            <option value="exponential">Exponential</option>
            <option value="inverse_power_Law">Inverse power law</option>
          </select>

          <label style={labelSize}> Domain size L x L</label>
            <div style={divTableCell}> <input type="text" style={domainInputform} /> x </div>  
            <div style={divTableCell}> <input type="text" style={domainInputformR} /> </div> 

          <label style={labelSize}> <InlineMath math={secondaryR0Label} /> : {secondaryR0} </label>
          <input type="range" min="0" max="20" value={secondaryR0}  onChange={e => setSecondaryR0(e.target.value)}/>   

          <label style={labelSize}> Infectious lifetime: {infectiousLT} </label>
          <input type="range" min="1" max="100" value={infectiousLT}  onChange={e => setInfectiousLT(e.target.value)}/> 

          <label style={labelSize}> Simulation Runtime: {simulationRT} </label>
          <input type="range" min="1" max="1000" value={simulationRT}  onChange={e => setSimulationRT(e.target.value)}/> 

          <input type="submit" value="Simulate" />
        </form>
      </div>
  );
}

export default InputParameters;
