import './App.css';
import React, { useState, useRef} from 'react';


function App() {
  const simulationPath = './media/sim.mp4'
  const catImagePath = './media/Thinking-of-getting-a-cat.png'

  const vidRef = useRef(null);
  const buttonRef = useRef(null);
  const [buttonText, setButtonText] = useState('>');
  const onButtonClick = () => {
    if (vidRef.current.paused) {
      vidRef.current.play()
      setButtonText('||')
    }
    else {
      vidRef.current.pause()
      setButtonText('>')
    }
  }

  const isSimGenerated = () => {
    try {
      const simulation = require('./media/sim.mp4')
      console.log('succces', simulation)
     return true;
    } catch (err) {
      console.log('fail')
     return false;
    }
  };

  const mySim = isSimGenerated()

  if  ( mySim == false) {
    console.log('Sim not generate, rendering cat')
    const catImg = require(catImagePath)  
    return (
      <div className="App">
        <header className="App-header">
        <div className='container'> 
          <div className='cube'> 
            <p> Image not here</p> 
            <image src={catImagePath}> </image>
          </div>
        </div>
        </header>
      </div>
    );
  }

  else {
    console.log('rendering vid')
    // import mySimX from './media/sim.mp4'
    const mySimX = require('./media/sim.mp4') 
    return (
      <div className="App">
        <header className="App-header">
        <div className='container'> 
          <div className='cube'> 
            <video ref={vidRef} src={mySimX} type="video/mp4"> </video> 
          </div>
        </div>

        <button ref={buttonRef} onClick={() => onButtonClick()}> {buttonText} </button>
        </header>
      </div>
    );
}}

export default App;
