import '../styles/app.css';
import 'katex/dist/katex.min.css';
import React, { useState } from 'react';
import { InlineMath } from 'react-katex';

const divTableCell = {
  display: 'table-cell',
};

const labelSize = {
  fontSize: 15
}
const domainInputform = {
  fontSize: 12, 
  padding: 1, 
  width: 30 ,
  marginLeft: 30}

  const domainInputformR = {
    fontSize: 12, 
    padding: 1, 
    width: 30,
    marginLeft: 4 }

function InputParameters() {

  const [message, setMessage] = useState("");
  const [dispersaType, setDispersal] = useState('gaussian');
  const [domainX, setDomainX] = useState(500);
  const [domainY, setDomainY] = useState(500);
  const [hostNumber, setHostNumber] = useState(1000);
  const [secondaryR0, setSecondaryR0] = useState(2);
  const [infectiousLT, setInfectiousLT] = useState(100);
  const [simulationRT, setSimulationRT] = useState(1000);
  const [initiallyInfected, setInitiallyInfected] = useState(10);
  const [initiallyInfectedDist, setInitiallyInfectedDist] = useState("Central");

  const secondaryR0Label = 'R\_{0}';

  let handleDomainInputX = (e) => {
    const re = /^[0-9\b]+$/;
    if (e.target.value === "" || re.test(e.target.value)) {
       setDomainX(e.target.value)
    }
    else {
      alert('Domain size requires integars!')
    }
 }

 let handleDomainInputY = (e) => {
  const re = /^[0-9\b]+$/;
  if (e.target.value === "" || re.test(e.target.value)) {
     setDomainY(e.target.value)
  }
  else {
    alert('Domain size requires integars!')
  }
}

 let handleSubmitResp = async (e) => {
    e.preventDefault();
    try {
      let res = await fetch("http://localhost:5000", 
      { 
        method: "POST",
        body: JSON.stringify({"dispersal_type": dispersaType,
                              "domain_size": [domainX, domainY],
                              "host_number": hostNumber,
                              "secondary_R0": secondaryR0,
                              "infectious_lifetime": infectiousLT,
                              "simulation_runtime": simulationRT,
                              "initially_infected_hosts": initiallyInfected,
                              "initially_infected_dist": initiallyInfectedDist})
      });
      console.log(res)
      if (res.status === 200) {
        setMessage("successfull");
        console.log('successfull post')
      } else {
        setMessage("error");
        console.log('failed failed')
      }
    } catch (err) {
      console.log(err);
      alert(`${message}, dispersal type is ${dispersaType}`)
    }
  };

  return (
      <div className='inputParamPanel'>
        <form onSubmit={handleSubmitResp} style={{width: 173}}>
          <label style={labelSize} > Dispersal type </label> 

          <select value={dispersaType} onChange={e => setDispersal(e.target.value)}> 
            <option value="gaussian">Gaussian</option>  
            <option value="exponential">Exponential</option>
            <option value="inverse_power_Law">Inverse power law</option>
          </select>
          <p></p>
          <label style={labelSize}> Domain size L x L</label>
            <div style={divTableCell}> <input type="text" style={domainInputform} value={domainX} onChange={handleDomainInputX} /> x </div>  
            <div style={divTableCell}> <input type="text" style={domainInputformR} value={domainY} onChange={handleDomainInputY} /> </div> 
          <br></br>
          <label style={labelSize}> Host Number : {hostNumber} </label>
          <input type="range" min="1" max="2000" value={hostNumber}  onChange={e => setHostNumber(e.target.value)}/>   
          <br></br>
          <label style={labelSize}> Secondary <InlineMath math={secondaryR0Label} /> : {secondaryR0} </label>
          <input type="range" min="0" max="20" value={secondaryR0}  onChange={e => setSecondaryR0(e.target.value)}/>   
          <br></br>
          <label style={labelSize}> Infectious lifetime: {infectiousLT} </label>
          <input type="range" min="1" max="500" value={infectiousLT}  onChange={e => setInfectiousLT(e.target.value)}/>  
          <br></br>
          <label style={labelSize}> Initially Infected: {initiallyInfected} </label>
          <input type="range" min="1" max="100" value={initiallyInfected}  onChange={e => setInitiallyInfected(e.target.value)}/> 
          <br></br>
          <label style={labelSize} > Initially Infected Distribution </label> 
          <select value={initiallyInfectedDist} onChange={e => setInitiallyInfectedDist(e.target.value)}> 
            <option value="Central"> Centralised</option>
            <option value="Random"> Random </option>  
          </select>
          <p></p>
          <label style={labelSize}> Time Steps: {simulationRT} </label>
          <input type="range" min="1" max="2000" value={simulationRT}  onChange={e => setSimulationRT(e.target.value)}/>
          <p></p>
          <input type="submit" value="Simulate" />
        </form>
      </div>
  );
}

export default InputParameters;
