import './App.css';
import myVideo from './media/file_example_MP4_480_1_5MG.mp4';


function App() {
  var video = document.getElementById("Video");
  console.log('video paused =',  video.paused)

  function vidplay(e) {
    console.log('e 1', e)
    console.log('video paused =',  video.paused)
    // e.preventDefault();
    var button = document.getElementById("play");
    if (video.paused) {
      video.play();
      button.textContent = "||";
    } else {
      video.pause();
      button.textContent = ">";
    }
  }

  // function updateProgressBar() { 
  //   var progressBar = document.getElementById('progressbar'); 
  //   var percentage = Math.floor((100 / video.duration) * video.currentTime);
  //   progressBar.value = percentage; progressBar.innerHTML = percentage + '% played'; 
  //   }

    // const [progress, updateProgressBar] = useState(0);

  // video.addEventListener('timeupdate', updateProgressBar, false);

  return (
    <div className="App">
      <header className="App-header">

        <video id='Video' src={myVideo}> </video>
        <button id="play" 
          onClick={vidplay} >  
          &gt;
        </button>

        <progress id="progressbar" value="0" max="100"></progress>

        <p>
          Sim text will goe here mofo...
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
