import './App.css';
import React, { useState, useRef} from 'react';


function InputParameters() {

  console.log('rendering input forms')
  const [name, setName] = useState("");
  const [dispersaType, setDispersal] = useState("");


  const handleSubmit = (e) => {
      // evt.preventDefault();
      setName(e.target.value)
      alert(`Submitting Name ${dispersaType}`)
  }

  return (
    <div className='inputParamPanel'>
    <form onSubmit={handleSubmit}>
      <label> 
        Dispersal type
        <select value={{name}} onChange={e => setDispersal(e.target.value)}> 
          <option value="gaussian">Gaussian</option>
          <option value="exponential">Exponential</option>
          <option value="inversePowerLaw">Inverse power law</option>
        </select>
      </label>

      <input type="submit" value="Simulate" />
    </form>
    </div>
  );
}

export default InputParameters;
