import './App.css';
import React, { useState, useRef} from 'react';
import myVideo from './media/file_example_MP4_480_1_5MG.mp4';


function App() {
  const vidRef = useRef(null);
    const buttonRef = useRef(null)

    const [buttonText, setButtonText] = useState('>')

    const onButtonClick = () => {
      if (vidRef.current.paused) {
        vidRef.current.play()
        setButtonText('||')
      }
      else {
        vidRef.current.pause()
        setButtonText('>')
      }
    }

  const isSimGenerated = () => {
    try {
      require('./media/Thinking-of-getting-a-cat.png')
      console.log('succces')
     return true;
    } catch (err) {
      console.log('fail')
     return null;
    }
  };


  if  ( isSimGenerated() == null) {
    console.log('Sim not generate')
    return (
      <div className="App">
        <header className="App-header">
        <div className='container'> 
          <div className='cube'> 
            <p> Image not here</p> 
          </div>
        </div>
        </header>
      </div>
    );
  }

  else {
    console.log('rendering vid')
    return (
      <div className="App">
        <header className="App-header">
        <div className='container'> 
          <div className='cube'> 
            <video ref={vidRef} src={myVideo} type="video/mp4"> </video> 
          </div>
        </div>

        <button ref={buttonRef} onClick={() => onButtonClick()}> {buttonText} </button>
        </header>
      </div>
    );
}}

export default App;
