import '../styles/app.css';
import React, { useState } from 'react';


function InputParameters() {

  const [message, setMessage] = useState("");
  const [dispersaType, setDispersal] = useState('gaussian');

  let handleSubmitResp = async (e) => {
    e.preventDefault();
    try {
      let res = await fetch("http://localhost:5000", 
      { 
        method: "POST",
        body: JSON.stringify({"dispersal type": dispersaType}),
      });
      console.log(res)
      if (res.status === 200) {
        setMessage("User created successfully");
      } else {
        setMessage("Some error occured");
        // todo address CORs issue on api gateway...
      }
    } catch (err) {
      console.log(err);
      alert(`${message}, dispersal type is ${dispersaType}`)
    }
  };

  return (
    <div className='inputParamPanel'>
    <form onSubmit={handleSubmitResp}>
      <label> Dispersal type
        <select value={dispersaType} onChange={e => setDispersal(e.target.value)}> 
          <option value="gaussian">Gaussian</option>  
          <option value="exponential">Exponential</option>
          <option value="inverse_power_Law">Inverse power law</option>
        </select>
        </label> 
        <label> Domain size 
          <input type="text" /> <input type="text" /> 
        </label> 

        <input type="submit" value="Simulate" />
    </form>
    </div>
  );
}

export default InputParameters;
