import '../styles/app.css';
import React, { useState } from 'react';


function InputParameters() {

  console.log('rendering input forms')
  const [name, setName] = useState("");
  const [dispersaType, setDispersal] = useState("guassian");
  const [message, setMessage] = useState("");

  let handelFormChage = (e) => {
      console.log(e)
      console.log('displersal type before: ', dispersaType)
      console.log('e target ', e.target.value)
      setDispersal(e.target.value)
      console.log('displersal type after: ', dispersaType)
  }
  let handleSubmitResp = async (e) => {
    e.preventDefault();
    try {
      console.log('updating dispersal:', dispersaType)
      let res = await fetch("https://9r5kx81fpi.execute-api.eu-west-2.amazonaws.com/dev/", 
      { 
        method: "POST",
        body: JSON.stringify({"dispersal type": dispersaType}),
      });
      if (res.status === 200) {
        console.log('response from back end mof', res)
        setMessage("User created successfully");
      } else {
        setMessage("Some error occured");
        // todo address CORs issue on api gateway...
      }
    } catch (err) {
      console.log(err);
      alert(`${message}, dispersal type is ${dispersaType}`)
    }
  };

  return (
    <div className='inputParamPanel'>
    <form onSubmit={handleSubmitResp}>
      <label> 
        Dispersal type
        <select value={{dispersaType}} onChange={handelFormChage}> 
          <option value="exponential">Exponential</option>
          <option value="gaussian">Gaussian</option>  
          <option value="inversePowerLaw">Inverse power law</option>
        </select>
      </label>

      <input type="submit" value="Simulate" />
    </form>
    </div>
  );
}

export default InputParameters;
