import './App.css';
import React, { useState, useRef} from 'react';
import { ProgressBar } from 'react-bootstrap';
import myVideo from './media/file_example_MP4_480_1_5MG.mp4';

function App() {

  const vidRef = useRef(null);

  const onButtonClick = () => {
    if (vidRef.current.paused) {
       vidRef.current.play()
    }
    else {
      vidRef.current.pause()
    }
  }


  const [progress, setProgressBar] = useState(0);

  const handelProgress = () => { 
    console.log(vidRef.current.duration)
    if (isNaN(vidRef.current.duration))   // duration is NotaNumber at Beginning.
      return;

    var percentage = Math.floor((100 / vidRef.current.duration) * vidRef.current.currentTime);
    setProgressBar(percentage); 
    console.log('current progress is ', percentage)

    // progressBar.innerHTML = percentage + '% played'; 
  }
  // function updateProgressBar() { 
  //   var progressBar = document.getElementById('progressbar'); 
  //   var percentage = Math.floor((100 / video.duration) * video.currentTime);
  //   progressBar.value = percentage; 
  //   
  //   }


  return (

    <div className="App">
      <header className="App-header">
      <video ref={vidRef}>
      </video>
        <video ref={vidRef} src={myVideo} type="video/mp4" onProgress={handelProgress}> </video>

        <button onClick={() => onButtonClick()} >  
          &gt;
        </button>

        <ProgressBar variant="success" now={40} />

        {/* <progress id="progressbar" value={progress} max="100" innerHTML={progress}></progress> */}

        {/* <p>
          Sim text will goe here mofo...
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a> */}
      </header>
    </div>
  );
}

export default App;
